const allItems = $input.all();
const lastItem = allItems[allItems.length - 1];

if (lastItem && Array.isArray(lastItem.json.messages)) {
  const messages = lastItem.json.messages;
  const count = messages.length;

  if (count === 0) return [{ json: { message: "" } }];

  const extractFirstLine = (text) => {
    if (!text) return "";
    return text.split('\n')[0].replace(/^Input from user:\s*/, '');
  };

  const trimEndNewline = (text) => {
    if (!text) return "";
    return text.replace(/\n+$/, '');
  };

  const selectedMessages = (count === 1) ? [messages[0]] : messages.slice(-1);

  const combinedMessage = selectedMessages.map((msg, idx) => {
    return `Message ${idx + 1}:\nhuman: ${extractFirstLine(msg.human)}\nai: ${trimEndNewline(msg.ai)}`;
  }).join('\n\n');

  return [{ json: { messages: combinedMessage } }];
}

return [{ json: { messages: "" } }];

return [
  {
    json: {
      success: true,
      status: 200,
      chatInput: $json.chatInput,
      response: JSON.stringify($json.output)
    }
  }
];

function cleanAndExtractJSON(response) {
    try {
        const result = {
            image_prompt: []
        };

        const lines = response.split('\n');
        let currentPrompt = '';

        for (const line of lines) {
            if (line.includes('"prompt":')) {
                if (currentPrompt) {
                    result.image_prompt.push(currentPrompt.trim());
                }
                currentPrompt = line.split('"prompt":')[1].trim();
            }
        }

        if (currentPrompt) {
            result.image_prompt.push(currentPrompt.trim());
        }

        return { json: result };

    } catch (error) {
        return { 
            json: {
                image_prompt: []
            }
        };
    }
}

const response = $input.first().json.output;
return cleanAndExtractJSON(response);

